import TechBanner from './TechBanner.js'
import CTABanner from './CTABanner.js'
import FeaturesBanner from './FeaturesBanner.js'
import PortSlider from './PortSlider.js'


export class Home extends Component{


    constructor() {
        super()
        this.state = { loading: true };
    }
    componentDidMount() {
        // this simulates an async action, after which the component will render the content
        this.setState({ loading: false });
      }


    render() {
        const { loading } = this.state;
        const settings = {
          dots: true,
          infinite: true,
          arrows: true,
          speed: 100,
          adaptiveHeight: true,
          autoplay: true,
          autoplaySpeed: 2200,
          slidesToShow: 3,
          slidesToScroll: 1
        };

        if (loading) {
          return (
            <div id="preloader">
              <div className="jumper">
                <div></div>
                <div></div>
                <div></div>
              </div>
            </div>
          )
        } else {
          return (
            <div className="">


              <Header scroll={this.fiscal} />
              <TechBanner />
