import React, { Component } from 'react';
import './App.css'
import '../assets/css/bootstrap.min.css'
import '../assets/css/font-awesome.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import fiscal_colour from '../assets/colours/fiscal.svg'
import fiscal from '../assets/mockups/headers/Sayblue.png'
import one from '../assets/mockups/fiscal/1.png'
import two from '../assets/mockups/fiscal/2.png'
import three from '../assets/mockups/fiscal/3.png'
import next from '../assets/images/next_white.svg'


export class Home extends Component {


  constructor() {
    super()
    this.state = { loading: true };
  }
  componentDidMount() {
    // this simulates an async action, after which the component will render the content
    this.setState({ loading: false });
  }


  render() {
    const { loading } = this.state;
    const settings = {
      dots: true,
      infinite: true,
      arrows: true,
      speed: 100,
      adaptiveHeight: true,
      autoplay: true,
      autoplaySpeed: 2200,
      slidesToShow: 3,
      slidesToScroll: 1
    };

    if (loading) {
      return (
        <div id="preloader">
          <div className="jumper">
            <div></div>
            <div></div>
            <div></div>
          </div>
        </div>
      )
    } else {
      return (
        <div>
          <div className="row" style={{
            padding: '0px', flex: 1,
            justifyContent: 'center',
            alignItems: 'center', marginLeft: 0, marginRight: 0
          }} >

            <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12 ">
              <img src={this.props.header_image} style={{ width: '100%', height: 'auto' }}></img>
            </div>
            <div className="offset-xl-1 offset-lg-1 col-xl-7 col-lg-7 col-md-8 col-sm-12 col-12 padding-bottom-40">
        <h1 className="padding-bottom-20  padding-top-80" style={{ paddingLeft: '15px', fontFamily: 'Ubuntu', fontWeight: 600, fontSize: '3em', letterSpacing: -1 }}>{this.props.title}</h1>
              <div className="col-xl-8 col-lg-8 col-md-12 col-sm-12 col-12">
                <h4 style={{ color: '#606060', fontFamily: 'Lato', fontWeight: '400', fontSize: '1em', lineHeight: '30px' }} >
                  {this.props.desc}
                </h4>
              </div>
            </div>
            <div className="col-xl-4 col-lg-4 col-md-4 col-sm-12 col-12">
            <a href="#contact" className="main-button-header" style={{backgroundColor:this.props.colour}}><img className="" style={{ width: '18pt', height: 'auto', marginRight: 10, marginBottom: 2 }} src={next}></img> View Project</a>

            </div>

          </div>
          <hr></hr>
          <div className="row" style={{
            padding: '0px', flex: 1,
            justifyContent: 'center',
            alignItems: 'center', marginLeft: 0, marginRight: 0
          }} >
            <div className="col-xl-8 col-lg-8 col-md-12 col-sm-12 col-12 padding-bottom-40">
              <h1 className="padding-bottom-20  padding-top-40" style={{ paddingLeft: '15px', fontFamily: 'Ubuntu', fontWeight: 600, fontSize: '2em', letterSpacing: -1 }}>Use Case</h1>
              <div className="col-xl-8 col-lg-8 col-md-12 col-sm-12 col-12">
                <h4 style={{ color: '#606060', fontFamily: 'Lato', fontWeight: '400', fontSize: '1em', lineHeight: '30px' }} >
                  {this.props.usecase}
                </h4>
              </div>
            </div>

          </div>
          <hr></hr>
          <div className="row" style={{
            padding: '0px', flex: 1,
            justifyContent: 'center',
            alignItems: 'center', marginLeft: 0, marginRight: 0
          }} >
            <div className="col-xl-8 col-lg-8 col-md-12 col-sm-12 col-12 padding-bottom-40">
              <h1 className="padding-bottom-20  padding-top-40" style={{ paddingLeft: '15px', fontFamily: 'Ubuntu', fontWeight: 600, fontSize: '2em', letterSpacing: -1 }}>Colour Scheme</h1>

              <div className="col-xl-6 col-lg-6 col-md-6 col-sm-12 col-12 ">
              <img src={this.props.colour_scheme} style={{width:'100%'}}></img>
              </div>
            </div>

          </div>
          <hr></hr>
          <div className="row" style={{
            padding: '0px', flex: 1,
            justifyContent: 'center',
            alignItems: 'center', marginLeft: 0, marginRight: 0
          }} >
            <div className="col-xl-8 col-lg-8 col-md-12 col-sm-12 col-12 padding-bottom-40">
              <h1 className="padding-bottom-20  padding-top-40" style={{ paddingLeft: '15px', fontFamily: 'Ubuntu', fontWeight: 600, fontSize: '2em', letterSpacing: -1 }}>Gallery</h1>
              <img src={this.props.port1} className="col-xl-4 col-lg-4 col-md-4 col-sm-12 col-12 padding-top-20"></img>
              <img src={this.props.port2} className="col-xl-4 col-lg-4 col-md-4 col-sm-12 col-12 padding-top-20"></img>
              <img src={this.props.port3} className="col-xl-4 col-lg-4 col-md-4 col-sm-12 col-12 padding-top-20"></img>

            </div>

          </div>
          <hr></hr>


        </div>


      );
    }
  }
}

export default Home;
