import React, { Component } from 'react';
import './components/App.css'
import './assets/css/bootstrap.min.css'
import './assets/css/font-awesome.css'
import NavBar from './NavBar.js'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Contact from './components/Contact.js'
import Header from './components/Header.js'
import Section1 from './components/Section1.js'
import Section2 from './components/Section2.js'
import Section3 from './components/Section3.js'
import TechBanner from './components/TechBanner.js'
import CTABanner from './components/CTABanner.js'
import PortSlider from './components/PortSlider.js'

import LogRocket from 'logrocket';



export class App extends Component {
  constructor() {
    super()
    this.state = { loading: true };
    LogRocket.init('0xjuad/clearweb-main-site');
  }

  componentDidMount() {
    // this simulates an async action, after which the component will render the content
    this.setState({ loading: false });
  }

  render() {
    const { loading } = this.state;
    const settings = {
      dots: true,
      infinite: true,
      arrows: true,
      speed: 100,
      adaptiveHeight: true,
      autoplay: true,
      autoplaySpeed: 2200,
      slidesToShow: 3,
      slidesToScroll: 1
    };

    if (loading) {
      return (
        <div id="preloader">
          <div className="jumper">
            <div></div>
            <div></div>
            <div></div>
          </div>
        </div>
      )
    } else {
      return (
        <div className="">

          <NavBar />
          <Header scroll={this.fiscal} />
          <TechBanner />
          <Section1 />
          <PortSlider />    
          {/*<Section3 />*/}

          <Section2 />
          <CTABanner/>
          <div id="contact"></div>
          {/*<div id="contact"  className="container">
            <hr></hr>
          </div>
          <hr style={{padding:'20px',paddingAbove:'40px'}}></hr>
        <div className="container">
        <div className="col-lg-12 col-md-12 col-12">
                      <h4 style={{ fontFamily: 'Source Sans Pro', paddingBottom:'20px',fontWeight:'600',fontSize: '16px',float:'right' }} className="">Copyright 2020 Clearweb &copy; All Rights Reserved.</h4>
                    </div>
    </div>*/}

        </div>
      );
    }
  }

}

export default App;
