import React, { Component } from 'react';
import './App.css'
import '../assets/css/bootstrap.min.css'
import '../assets/css/font-awesome.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import fiscal from '../mockups/FiscalFresh.png'



export class Home extends Component {


  constructor() {
    super()
    this.state = { loading: true };
  }
  componentDidMount() {
    // this simulates an async action, after which the component will render the content
    this.setState({ loading: false });
  }


  render() {
    const { loading } = this.state;
    const settings = {
      dots: true,
      infinite: true,
      arrows: true,
      speed: 100,
      adaptiveHeight: true,
      autoplay: true,
      autoplaySpeed: 2200,
      slidesToShow: 3,
      slidesToScroll: 1
    };

    if (loading) {
      return (
        <div id="preloader">
          <div className="jumper">
            <div></div>
            <div></div>
            <div></div>
          </div>
        </div>
      )
    } else {
      return (
        <div className="row" style={{
          padding: '0px', flex: 1,
          justifyContent: 'center',
          alignItems: 'center', marginLeft: 0, marginRight: 0
        }} >

          <div  className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12 ">
          <img src={fiscal} style={{width:'100%',height:'200px'}}></img>
          </div>
          <div  className="col-xl-6 col-lg-6 col-md-12 col-sm-12 col-12 ">
            <h1 className="padding-bottom-20 padding-top-80 " style={{ paddingLeft: '15px', fontFamily: 'Ubuntu', fontWeight: 600, fontSize: '3em', letterSpacing: -1 }}>Fiscal Fresh</h1>
            <div className="col-xl-10 col-lg-10 col-md-12 col-sm-12 col-12">
              <div className="row ">

                <h4 style={{ paddingLeft: '15px', color: '#606060', fontFamily: 'Lato', fontWeight: '400', fontSize: '1em', lineHeight: '30px' }} >
                  We are a full service Web Design and Development agency based in Toronto, Canada.
                  We focus on transparency throughout the entire development process, from concept design to website deployment,
                  you’ll know everything about your webpage. Check our recent portfolio of interactive sites and contact us if
                  you’d like to increase your online presence today!
    </h4>


              </div>

            </div>
            <div style={{ paddingLeft: '30px' }} className="padding-top-40 col-xl-10 col-lg-12 col-md-12 col-sm-12 col-12">

              <div className="row">

              </div>

            </div>
          </div>

        </div>


      );
    }
  }
}

export default Home;
