import React, { Component } from 'react';
import './App.css'
import '../assets/css/bootstrap.min.css'
import '../assets/css/font-awesome.css'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import fiscal from '../assets/images/FiscalHeader.png'
import one from '../assets/images/1.svg'



export class Home extends Component {


  constructor() {
    super()
    this.state = { loading: true };
  }
  componentDidMount() {
    // this simulates an async action, after which the component will render the content
    this.setState({ loading: false });
  }


  render() {
    const { loading } = this.state;
    const settings = {
      dots: true,
      infinite: true,
      arrows: true,
      speed: 100,
      adaptiveHeight: true,
      autoplay: true,
      autoplaySpeed: 2200,
      slidesToShow: 3,
      slidesToScroll: 1
    };

    if (loading) {
      return (
        <div id="preloader">
          <div className="jumper">
            <div></div>
            <div></div>
            <div></div>
          </div>
        </div>
      )
    } else {
      return (
        <div>
          <div className="row" style={{
            padding: '0px', flex: 1,
            justifyContent: 'center',
            alignItems: 'center', marginLeft: 0, marginRight: 0
          }} >

            <div className="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12 ">
              <img src={fiscal} style={{ width: '100%', height: 'auto' }}></img>
            </div>
            <div className="col-xl-6 col-lg-6 col-md-12 col-sm-12 col-12 ">
              <h1 className="padding-bottom-20 padding-top-80 " style={{ paddingLeft: '15px', fontFamily: 'Ubuntu', fontWeight: 600, fontSize: '3em', letterSpacing: -1 }}>Fiscal Fresh</h1>
              <div className="col-xl-10 col-lg-10 col-md-12 col-sm-12 col-12">
                <div className="row ">

                  <h4 style={{ paddingLeft: '15px', color: '#606060', fontFamily: 'Lato', fontWeight: '400', fontSize: '1em', lineHeight: '30px' }} >
                    Browse and search millions of healthy recipes, create a meal plan then continue to checkout. Required groceries will be auto added to Instacart checkout.
                    This web application aims to cut out the middle man of meal delivery services and brings the savings back to the consumer.
    </h4>


                </div>

              </div>
              <div style={{ paddingLeft: '30px' }} className="padding-top-40 col-xl-10 col-lg-12 col-md-12 col-sm-12 col-12">

                <div className="row">

                </div>

              </div>
            </div>

          </div>
          <div style={{ marginTop: '0px', backgroundImage: 'none', paddingBottom: 100 }} >
            <div className="header-text ">
              <div style={{
                padding: '0px', flex: 1,
                justifyContent: 'center',
                alignItems: 'center', marginLeft: 0, marginRight: 0
              }} className="row" >


                <div className="offset-lg-1 col-xl-3 col-lg-3 col-md-12 col-sm-12 ">
                  <img className="landing-img " style={{ width: '100%', height: 'auto', marginTop: 80,padding:'20%' }} src={one}></img>
                </div>
                <div className=" col-xl-6 col-lg-6 col-md-12 col-sm-12 col-12 ">
                  <h1 className="padding-bottom-20 padding-top-80" style={{ paddingLeft: '15px', fontFamily: 'Ubuntu', fontWeight: 600, fontSize: '3em' }}>Responsive Web Design</h1>
                  <div className="col-xl-10 col-lg-12 col-md-12 col-sm-12 col-12">
                    <div className="row">

                      <h4 style={{ paddingLeft: '15px', fontFamily: 'Lato', color: '#606060', fontWeight: '400', fontSize: '1em', lineHeight: '30px' }} >
                        Our designers map out efficient web pages to interact intelligently with your target audience and maximize your ROI.
                   Clearwebâ€™s modern design style runs smoothly on both desktop and mobile.</h4>

                    </div>
                    <div style={{ paddingLeft: '15px' }} className="padding-top-40">

                      <div className="row">

                      </div>

                    </div>
                  </div>
                </div>



              </div>
            </div>
          </div>
        </div>


      );
    }
  }
}

export default Home;
